function drawLevels()

  if level == 1 then
    for j = 1, #levelMap1 do
      local row = levelMap1[j]
      for k = 1, #row do
        if levelMap1[j][k] == 1 then -- if the tile is a 1 then we color it gray in this example.
          love.graphics.setColor(.5, .5, .5)
          --love tables start at 1 but the window dimensions start at 0, so we minus 1 to start at 0. it's weird but that's how it goes.
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        elseif levelMap1[j][k] == 2 then --if the tile is 2 it's black.
          love.graphics.setColor(0, 0, 0)
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        elseif levelMap1[j][k] == 0 then --if the tile is 0 it's white.
          love.graphics.setColor(1, 1, 1)
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        end -- if
      end -- for
    end -- for
  elseif level == 2 then
    for j = 1, #levelMap2 do
      local row = levelMap2[j]
      for k = 1, #row do
        if levelMap2[j][k] == 1 then -- if the tile is a 1 then we color it green in this example.
          love.graphics.setColor(0.5, 0.5, 0.5)
          --love tables start at 1 but the window dimensions start at 0, so we minus 1 to start at 0. it's weird but that's how it goes.
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        elseif levelMap2[j][k] == 2 then --if the tile is 0 it's yellow.
          love.graphics.setColor(0, 0, 0)
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        elseif levelMap2[j][k] == 0 then --if the tile is 0 it's yellow.
          love.graphics.setColor(1, 1, 1)
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        end -- if
      end -- for
    end -- for
  elseif level == 3 then
    for j = 1, #levelMap3 do
      local row = levelMap3[j]
      for k = 1, #row do
        if levelMap3[j][k] == 1 then -- if the tile is a 1 then we color it green in this example.
          love.graphics.setColor(0.5, 0.5, 0.5)
          --love tables start at 1 but the window dimensions start at 0, so we minus 1 to start at 0. it's weird but that's how it goes.
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        elseif levelMap3[j][k] == 2 then --if the tile is 0 it's yellow.
          love.graphics.setColor(0, 0, 0)
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        elseif levelMap3[j][k] == 0 then --if the tile is 0 it's yellow.
          love.graphics.setColor(1, 1, 1)
          love.graphics.rectangle('fill', (k - 1) * TILE_SIZE, (j - 1) * TILE_SIZE, TILE_SIZE, TILE_SIZE)
        end -- if
      end -- for
    end -- for
  end -- if
end -- function
